import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';

class App extends Component {
  render(){

    return(
      <div class="col">
        <div class="row1">
          <Strbox/>
        </div>
        <div class="row2">
          <DFA/>
        </div>
      </div>
    );
  }
}

class Strbox extends Component{
  constructor(prop){
    super(prop);


    this.state={
      ip:"",
      isboxEn:true
    }

    this.handlebox = this.handlebox.bind(this);
    this.handleRand = this.handleRand.bind(this);
  }

  render(){
    return(
      <div>
        <h2>Put string here</h2>
        <input type="text" onChange={this.handlebox} disabled={this.isboxEn}>      
        </input>
        <button type="button" onClick={this.handleRand}>Random<br></br>String</button>{' '}
        <br></br>
        <button variant="primary" onClick>Start</button>
        <button>Reset</button>
        <button>Previous</button>
        <button>Read Next</button>
        <button>Read All</button>
        <br></br>
        <h1>{this.state.ip}</h1>
      </div>
    );
  }

  handlebox(event){
    this.setState({ip:event.target.value})
  }

  handleStart(event){
    this.setState({isboxEn:false})
  }

  handleReset(event){
    this.setState({isboxEn:true})
  }

  handleRand(event){
    var allcha = 'abcde';
    var len = 8;
    var result = '';
    for(let i=0;i<len;i++){
      result += allcha.charAt(Math.floor(Math.random()*100)%5)
    }
    this.setState({ip:result})
  }
}

class DFA extends Component{
  render(){
    return(
      <div>
        DFA diagram is supposed to be here
      </div>
    );
  }
}


export default App;
