import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';

class App extends Component {
  render(){

    return(
      <div class="col">
        <div class="row1">
          <Strbox/>
        </div>
        <div class="row2">
          <DFA/>
        </div>
      </div>
    );
  }
}
class Strbox extends Component{
  constructor(prop){
    super(prop);


    this.state={
      ip:""
    }

    this.handleChange = this.handleChange.bind(this);
  }

  render(){
    return(
      <div>
        <h2>Put string here</h2>
        <input type="text" onChange={this.handleChange} >      
        </input>
        <button type="button" onClick="handleRand">Random<br></br>String</button>
        <br></br>
        <button>Start</button>
        <button>Reset</button>
        <button>Previous</button>
        <button>Read Next</button>
        <button>Read All</button>
        <br></br>
        {this.state.ip}
      </div>
    );
  }

  handleChange(event){
    this.setState({ip:event.target.value})
  }
}

class DFA extends Component{
  render(){
    return(
      <div>
        DFA diagram is supposed to be here
      </div>
    );
  }
}


export default App;
