import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';

const pic1 = require('./pic/1.jpg');
const pic2 = require('./pic/2.jpg');
const pic3 = require('./pic/3.jpg');
const pic4 = require('./pic/4.jpg');
const pic5 = require('./pic/5.jpg');
const pic6 = require('./pic/6.jpg');
const pic7 = require('./pic/7.jpg');
const pic8 = require('./pic/8.jpg');
const pic9 = require('./pic/9.jpg');
const pic10 = require('./pic/10.jpg');
const pic11 = require('./pic/11.jpg');


class App extends Component {
  constructor(prop){
    super(prop);


    this.state={
      ip:"",
      ptr:0,
      cur:0
    }

    this.handlebox = this.handlebox.bind(this);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
  }
  render(){

    return(
      <div class="col">
        <div class="row1">
          <Strbox value={this.state.ip} 
          updatebox={this.handlebox}
          next={this.next}
          prev={this.previous}
          ptr={this.state.ptr}
          />
        </div>
        <div class="row2">
          <DFA ptr={this.state.ptr}/>
        </div>
      </div>
    );
  }

  handlebox(v){
    this.setState({ip:v}); 
  }

  next(){
    this.setState({ptr:this.state.ptr + 1});
  }

  previous(){
    this.setState({ptr:this.state.ptr - 1});
  }

  startread(){
    let str = this.state.ip //l e m 1 2 i h t c
    let n = str[this.state.ptr+1]
    switch(str[this.state.ptr]){
      case 'l': {this.coffee(n); break;}
      case 'e': {this.coffee(n); break;}
      case 'm': {this.coffee(n); break;}
      case '1': {this.lone(n); break;}
      case '2': {this.ltwo(n); break;}
      case 'i': {this.ice(); break;}
      case 'h': {this.hot(); break;}
      case 't': {this.tang(); break;}
      case 'c': {this.cancel(); break;}
