    this.state={
      ip:"",
      ptr:0
    }

    this.handlebox = this.handlebox.bind(this);
  }
  render(){

    return(
      <div class="col">
        <div class="row1">
          <Strbox value={this.state.ip} updatebox={this.handlebox} read={this.state.read}/>
        </div>
        <div class="row2">
          <DFA/>
        </div>
      </div>
    );
  }

  handlebox(v){
    this.setState({ip:v})    
