import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';

const init = require('./pic/1.jpg');
const moc = require('./pic/2.jpg');
const esp = require('./pic/3.jpg');
const latt = require('./pic/4.jpg');
const lv1 = require('./pic/5.jpg');
const lv2 = require('./pic/6.jpg');
const ice = require('./pic/7.jpg');
const hot = require('./pic/8.jpg');
const money = require('./pic/9.jpg');
const fin = require('./pic/10.jpg');
const trap = require('./pic/11.jpg');


class App extends Component {
  constructor(prop){
    super(prop);


    this.state={
      ip:"",
      ptr:0,
      cur:'0'
    }

    this.handlebox = this.handlebox.bind(this);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
  }
  render(){

    return(
      <div class="col">
        <div class="row1">
          <Strbox value={this.state.ip} 
          updatebox={this.handlebox}
          next={this.next}
          prev={this.previous}
          ptr={this.state.ptr}
          />
        </div>
        <div class="row2">
          <DFA cur={this.state.cur}/>
        </div>
      </div>
    );
  }

  handlebox(v){
    this.setState({ip:v}); 
  }

  next(){
    this.startread();
    this.setState({ptr:this.state.ptr + 1});
  }

  previous(){
    if (this.state.ptr > 0){
      this.startread();
      this.setState({ptr:this.state.ptr - 1});
    }
  }

  startread(){
    if(this.state.ptr == 0){
      var str ='0';
    }
    else{
      var str = this.state.ip; //l e m 1 2 i h t c
      str[this.state.ptr-1] 
    }
    let n = str[this.state.ptr];
    switch(str[this.state.ptr-1]){
      case null: 
      case 'l': 
      case 'e': 
      case 'm': {this.coffee(n); break;}
      case '1': 
      case '2': {this.level(n); break;}
      case 'i': 
      case 'h': {this.icehot(n); break;}
      case 't': {this.tang(n); break;}
      case 'c': {this.cancel(); break;}
      default: 
    }
  }

  init(n){
    switch(n){
      case 'l': {this.setState({cur:'l'}); break;}
      case 'e': {this.setState({cur:'e'}); break;}
      case 'm': {this.setState({cur:'m'}); break;}
      default: 
    }
  }

  coffee(n){
    switch(n){
      case 'l': {this.setState({cur:'l'}); break;}
      case 'e': {this.setState({cur:'e'}); break;}
      case 'm': {this.setState({cur:'m'}); break;}
      case '1': {this.setState({cur:'1'}); break;}
      case '2': {this.setState({cur:'2'}); break;}
      default:
    }
  }


  level(){

  }


  icehot(){

  }


  tang(){

  }

  cancel(){

  }
}

class Strbox extends Component{
  constructor(prop){
    super(prop);

    this.state={
      isboxEn:false
    }

    this.handleRand = this.handleRand.bind(this);
    this.handleStart = this.handleStart.bind(this);
    this.handleReset = this.handleReset.bind(this);
    this.update = this.update.bind(this);
  }

  render(){
    return(
      <div>
        <h2>Put string here</h2>
        <input type="text" value ={this.props.value} onChange={this.update} disabled={this.state.isboxEn}>      
        </input>
        <button type="button" onClick={this.handleRand}>Random<br></br>String</button>{' '}
        <br></br>
        <button variant="primary" onClick={this.handleStart}>Start</button>
        <button onClick={this.handleReset}>Reset</button>
        <button onClick={this.props.prev}>Previous</button>
        <button onClick={this.props.next}>Read Next</button>
        <button >Read All</button>
        <br></br>
        <h1>{this.props.value}</h1>
        <h1>{this.props.ptr}</h1>
      </div>
    );
  }
  update(event){
    this.props.updatebox(event.target.value);
  }

  handleStart(event){
    this.setState({isboxEn:true});
  }

  handleReset(event){
    this.setState({isboxEn:false});
  } 

  handleRand(event){    
    if(!this.state.isboxEn){
      var allcha = 'abcde';
      var len = 8;
      var result = '';
      for(let i=0;i<len;i++){
        result += allcha.charAt(Math.floor(Math.random()*100)%5)
      }
      this.props.updatebox(result)
    }
  }
}

class DFA extends Component{

  render(){
    return (this.set());
  }


  set(){
    switch(this.props.cur){

      case 'l':{
        return(
        <div>
            <img src={latt}/>
        </div>
      );}
      case 'm':{
        return(
        <div>
            <img src={moc}/>
        </div>
      );}
      case 'e':{
        return(
        <div>
            <img src={esp}/>
        </div>
      );}
      case '1':{
        return(
        <div>
            <img src={lv1}/>
        </div>
      );}
      case '2':{
        return(
        <div>
            <img src={lv2}/>
        </div>
      );}
      case 'i':{
        return(
        <div>
            <img src={ice}/>
        </div>
      );}
      case 'h':{
        return(
        <div>
            <img src={hot}/>
        </div>
      );}
      case 't':{
        return(
        <div>
            <img src={money}/>
        </div>
      );}
      case 'f':{
        return(
        <div>
            <img src={fin}/>
        </div>
      );}
      case 'trap':{
        return(
        <div>
            <img src={trap}/>
        </div>
      );}
      default:{
        return(
        <div>
            <img src={init}/>
        </div>
      );}
    }
  }

}


export default App;
