import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';

const pic1 = require('./pic/1.jpg');



class App extends Component {
  constructor(prop){
    super(prop);


    this.state={
      ip:"",
      ptr:0
    }

    this.handlebox = this.handlebox.bind(this);
    this.next = this.next.bind(this);
    this.previous = this.previous.bind(this);
  }
  render(){

    return(
      <div class="col">
        <div class="row1">
          <Strbox value={this.state.ip} 
          updatebox={this.handlebox}
          next={this.next}
          prev={this.previous}
          ptr={this.state.ptr}
          />
        </div>
        <div class="row2">
          <DFA pic1/>
        </div>
      </div>
    );
  }

  handlebox(v){
    this.setState({ip:v}); 
  }

  next(){
    this.setState({ptr:this.state.ptr + 1});
  }

  previous(){
    this.setState({ptr:this.state.ptr - 1});
  }

  startread(){
    let str = this.state.ip //l e m 1 2 i h
    switch(str[this.state.ptr]){
      case 'l': this.l(); break;
      case 'e': this.e(); break;
      case 'm': this.m(); break;
      case '1': this.one(); break;
      case '2': this.two(); break;
      case 'i': this.i(); break;
      case 'h': this.h(); break;
      default: this.trap()
    }
  }

  trap(){

  }

  l(){

  }

  e(){

  }

  m(){

  }

  one(){

  }

  two(){

  }

  i(){

  }

  h(){

  }
}

class Strbox extends Component{
  constructor(prop){
    super(prop);

    this.state={
      isboxEn:false
    }

    this.handleRand = this.handleRand.bind(this);
    this.handleStart = this.handleStart.bind(this);
    this.handleReset = this.handleReset.bind(this);
    this.update = this.update.bind(this);
  }

  render(){
    return(
      <div>
        <h2>Put string here</h2>
        <input type="text" value ={this.props.value} onChange={this.update} disabled={this.state.isboxEn}>      
        </input>
        <button type="button" onClick={this.handleRand}>Random<br></br>String</button>{' '}
        <br></br>
        <button variant="primary" onClick={this.handleStart}>Start</button>
        <button onClick={this.handleReset}>Reset</button>
        <button onClick={this.props.prev}>Previous</button>
        <button onClick={this.props.next}>Read Next</button>
        <button >Read All</button>
        <br></br>
        <h1>{this.props.value}</h1>
        <h1>{this.props.ptr}</h1>
      </div>
    );
  }
  update(event){
    this.props.updatebox(event.target.value);
  }

  handleStart(event){
    this.setState({isboxEn:true});
  }

  handleReset(event){
    this.setState({isboxEn:false});
  } 

  handleRand(event){    
    if(!this.state.isboxEn){
      var allcha = 'abcde';
      var len = 8;
      var result = '';
      for(let i=0;i<len;i++){
        result += allcha.charAt(Math.floor(Math.random()*100)%5)
      }
      this.props.updatebox(result)
    }
  }
}

class DFA extends Component{
  render(){
    return(
      <div>
        DFA diagram is supposed to be here
        <img src={pic1}/>
      </div>
    );
  }
}


export default App;
